new CfnFunction(stack, "MyLambda", {
handler: 'handler',
runtime: 'nodejs22.x',
role: lambdaRole.attrArn,
code: {
zipFile: `
exports.handler = async(args) => {
let response = {
statusCode: 200,
body: JSON.stringify("Hello From Lambda")
};
return response;
}
`
}
});

const lambdaRole = new CfnRole(stack, "LambdaRole", {
assumerolepolicydocument: {
Version: '2012-10-17',
Statement: [
{
Effect: 'Allow',
Principal: {
Service: [
'lambda.amazonaws.com'
]
},
Action: [
'sts.AssumeRole'
]
}
]
}
});
